class TermsModel {
  TermsModel({
    String? status,
    String? message,
    String? pageData,
  }) {
    _status = status;
    _message = message;
    _pageData = pageData;
  }

  TermsModel.fromJson(dynamic json) {
    _status = json['status'];
    _message = json['message'];
    _pageData = json['pageData'];
  }
  String? _status;
  String? _message;
  String? _pageData;
  TermsModel copyWith({
    String? status,
    String? message,
    String? pageData,
  }) =>
      TermsModel(
        status: status ?? _status,
        message: message ?? _message,
        pageData: pageData ?? _pageData,
      );
  String? get status => _status;
  String? get message => _message;
  String? get pageData => _pageData;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['status'] = _status;
    map['message'] = _message;
    map['pageData'] = _pageData;
    return map;
  }
}
